var BanList = new Array();
var BanListFilter = "";

function LoadBans(){ 
	var xmlHttp = new XMLHttpRequest();
    xmlHttp.onreadystatechange = function() { 
        if (xmlHttp.readyState == 4 && xmlHttp.status == 200) {
			BanList = JSON.parse(xmlHttp.responseText);
			var bansloading = document.getElementById('bansloading');
			bansloading.remove();
			var bandiv1 = document.createElement("div");
			bandiv1.className = 'form-group';
			var bandiv2 = document.createElement("div");
			bandiv2.className = 'input-group';
			var banspan = document.createElement("span");
			banspan.className = 'input-group-addon';
			var bani = document.createElement("i");
			bani.className = 'fa fa-search';
			var baninput = document.createElement("input");
			baninput.className = "form-control";
			baninput.type = "search";
			baninput.value = "";
			baninput.placeholder = "Поиск по имени или steamid";
			baninput.oninput = function() {
				BanListFilter = baninput.value.toLowerCase();
				LoadBansPage(1);
			};
			
			bandiv1.appendChild(bandiv2);
			bandiv2.appendChild(banspan);
			bandiv2.appendChild(baninput);
			banspan.appendChild(bani);
			
			document.getElementsByClassName('modal-body')[0].appendChild(bandiv1);
			
			LoadBansPage(1);
			
		}
    }
    xmlHttp.open("GET", "https://api.magicrust.ru/players/getBanList.php", true);
    xmlHttp.send(null);
}

function LoadBansPage(page) {
	
	var bans0 = document.getElementById("bansssblyat");
	if(bans0 != null) {
		getParent(bans0).removeChild(bans0);
	}
	
	var n = page;
	if(page < 1) {n = 1;}
	else if(page >= BanList.filter(BansFilter).length) {n = Math.floor(BanList.filter(BansFilter).length/10)*10+1;}
	
	var bans1 = document.createElement("div");
	bans1.id = "bansssblyat";
	bans1.className = "table-wrapper";
	var bans2 = document.createElement("div");
	bans2.className = "table-body";
	var bans3 = document.createElement("table");
	bans3.className = "table table-striped";
	
	bans3.innerHTML = '<thead><tr><th class="sorting_disabled">Игрок</th><th class="sorting_disabled">Причина</th><th class="sorting_disabled" width="20%">Дата</th><th class="sorting_disabled" width="1%" title="Бан по айпи">IP</th></tr></thead>';
	var bans4 = document.createElement("tbody");
	bans1.appendChild(bans2);
	bans2.appendChild(bans3);
	bans3.appendChild(bans4);
	var max = n+9;
	if(max > BanList.filter(BansFilter).length) {max = BanList.filter(BansFilter).length;}
	for (var i = n-1; i < max; i++) {
		var banip = '';
		if(BanList.filter(BansFilter)[i].banip == 1) {banip = '<i class="fa fa-check" title="Игрок забанен по ip адресу!"></i>';}
		var nickname = BanList.filter(BansFilter)[i].nickname;
		if(nickname == "") {nickname = "<font color=red>Неизвестно</font>";}
		bans4.innerHTML = bans4.innerHTML+'<tr><td><a href="https://steamcommunity.com/profiles/'+BanList.filter(BansFilter)[i].steamid+'">'+nickname+'</a></td><td>'+BanList.filter(BansFilter)[i].reason+'</td><td>'+timeConverter(BanList.filter(BansFilter)[i].time)+'</td><td>'+banip+'</td>';
		
	}
	
	var bans5 = document.createElement("div");
	bans5.className = "table-footer";
	var bans6 = document.createElement("div");
	bans6.className = "pagination";
	bans1.appendChild(bans5);
	bans5.appendChild(bans6);
	
	var bansa1 = document.createElement("a");
	bansa1.className = "page-link";
	bansa1.href = "#";
	bansa1.innerHTML = "&lt;&lt;";
	bansa1.onclick = function() {LoadBansPage(1);}
	var bansa2 = document.createElement("a");
	bansa2.className = "page-link";
	bansa2.href = "#";
	bansa2.innerHTML = "&lt;";
	bansa2.onclick = function() {LoadBansPage(n-10);}
	var bansa3 = document.createElement("a");
	bansa3.className = "page-link";
	bansa3.href = "#";
	bansa3.innerHTML = n+'-'+max;
	var bansa4 = document.createElement("a");
	bansa4.className = "page-link";
	bansa4.href = "#";
	bansa4.innerHTML = "&gt;";
	bansa4.onclick = function() {LoadBansPage(n+10);}
	var bansa5 = document.createElement("a");
	bansa5.className = "page-link";
	bansa5.href = "#";
	bansa5.innerHTML = "&gt;&gt;";
	bansa5.onclick = function() {LoadBansPage(Math.floor(BanList.length/10)*10+1);}
	
	bans6.appendChild(bansa1);
	bans6.appendChild(bansa2);
	bans6.appendChild(bansa3);
	bans6.appendChild(bansa4);
	bans6.appendChild(bansa5);
	
	
	document.getElementsByClassName('modal-body')[0].appendChild(bans1);
	
	
}

function BansFilter(value) {
  return (value.nickname.toLowerCase().indexOf(BanListFilter) >= 0 || value.steamid.indexOf(BanListFilter) >= 0);
}

function LoadPlayerCounter() {
    var x2 = new XMLHttpRequest();
	x2.open("GET", "//api.magicrust.ru/players/getPlayerCount.php", true);
	x2.onload = function (){
		var playerCounterBlock = document.getElementById('playerscounter');
        var amount = Number(x2.responseText);
		playerCounterBlock.innerHTML = '<div class="xserver"><div class="xserver__title">Уникальные игроки</div><div class="xserver__status"><div class="progress"><div class="progress-bar progress-bar-animated bg-success progress-bar-striped" role="progressbar" aria-valuenow="'+amount+'" aria-valuemin="0" aria-valuemax="400000" style="width: '+(amount/400000*100)+'%;"></div></div></div><div class="xserver__links">'+amount+' / 400000</div></div>';
	}
	x2.send(null);
}

function timeConverter(UNIX_timestamp){
	var a = new Date(UNIX_timestamp * 1000);
	var months = ['Января','Февраля','Марта','Апреля','Мая','Июня','Июля','Августа','Сентября','Октября','Ноября','Декабря'];
	var year = a.getFullYear();
	var month = months[a.getMonth()];
	var date = a.getDate();
	var hour = a.getHours();
	var min = a.getMinutes();
	if(min < 10) {min = "0"+min;}
	var sec = a.getSeconds();
	var time = '<span title="'+hour + ':' + min+'">' + date + ' ' + month + ' ' + year + '</span>';
	return time;
}

window.onload = function () {
	LoadPlayerCounter();
	document.body.onclick=function(event)
	{
		if(event.target.id == 'blet') {closeExt();}
		else if(event.target.id == 'extra-faq') {LoadPage("faq");}
		else if(event.target.id == 'extra-kits') {LoadPage("kits");}
		else if(event.target.id == 'extra-blocks') {LoadPage("blocks");}
		else if(event.target.id == 'extra-servers') {LoadPage("servers");}
		else if(event.target.id == 'extra-help') {LoadPage("help");}
		else if(event.target.id == 'extra-rules') {LoadPage("rules");}
		else if(event.target.id == 'extra-bans') {LoadPage("bans");}
	}
	
	if(window.location.pathname == "/rules") {LoadPageOld('rules');}
	else if(window.location.pathname == "/help") {LoadPageOld('help');}
	else if(window.location.pathname == "/kits") {LoadPageOld('kits');}
	else if(window.location.pathname == "/faq") {LoadPageOld('faq');}
	else if(window.location.pathname == "/servers") {LoadPageOld('servers');}
	else if(window.location.pathname == "/blocks") {LoadPageOld('blocks');}
	else if(window.location.pathname == "/bans") {LoadPageOld('bans');}
}
